mapper.update:
	update
		portal_data
	set
		thumbnail = #{thumbnail}
	where
		portal_idx = #{portal_idx}

mapper.select:
select 
	* 
from 
	naver_videos 
where url = #{url} 
order by createDate asc limit 1;

select 
	* 
from 
	naver_videos_detail 
where url = #{url} 
order by createDate asc limit 1;

mapper.update:
update
	naver_videos
set
	portal_subtitle = #{portal_subtitle}
where
	url = #{url};

update
	naver_videos_detail
set
	portal_subtitle = #{portal_subtitle}
where
	url = #{url};
