mapper.select:
select count(if(textType='좋은글', textType, null)) lik, count(if(textType='나쁜글', textType, null)) dis, count(if(textType='관심글', textType, null)) cu, count(if(textType='기타글', textType, null)) etc, count(if(textType is not null and texttype!='삭제글', textType, null)) al from union.media_data where textType is not null
and keyword = 'press'
and left(writeDate,10) >= #{startDate} and left(writeDate,10) <= #{endDate}

select count(*) from media_data where textType is not null and textType!='삭제글'
and writeDate >= CURDATE()
and writeDate > #{startDate} and writeDate < #{endDate}

 		select 
 			count(*) 
 		from 
 			media_data
 		where
 			media_name = #{keyword}
 		and
			textType!='삭제글'
 		
			and updateDate > #{startDate}
			and updateDate < #{endDate}

 		select 
 			count(*) 
 		from 
 			media_data
 		where
 			reporter_name = #{keyword}
 		
			and updateDate > #{startDate}
			and updateDate < #{endDate}

 		select 
 			writeDate, media_name, media_title, keyword, textType
 		from 
 			media_data
 		where
 			reporter_name = #{reporter}
 		and
 			textType = #{textType}	
 		
			and updateDate > #{startDate}
			and updateDate < #{endDate}

mapper.update:
	update
		media_data
	set
		thumbnail = #{thumbnail}
	where
		media_idx = #{media_idx}

mapper.select:
select a.media_name,a.total as match_total, b.total as match_total2 from (select media_name,count(*)as total from media_data WHERE media_name !='naver' and media_name !='daum' and media_name !='쇼박스'
group by media_name order by total desc) as a left join (select media_name,count(*)as total from media_data where media_name !='naver' and media_name !='daum' and media_name !='쇼박스' and keyword != 'press'
group by media_name order by total desc) as b on a.media_name = b.media_name order by match_total desc limit 10;

select a.reporter_name,a.media_name,a.total as match_total, b.total as match_total2 from (select reporter_name,media_name,count(*)as total from media_data WHERE media_name !='naver' and media_name !='daum' and media_name !='쇼박스' and reporter_name is not null
group by reporter_name order by total desc) as a left join (select reporter_name,media_name,count(*)as total from media_data where media_name !='naver' and media_name !='daum' and media_name !='쇼박스' and keyword != 'press' and reporter_name is not null
group by reporter_name order by total desc) as b on a.reporter_name = b.reporter_name order by match_total desc limit 10;
